export default function Suggestion() {
    const [openSuggestion, setopenSuggestion] = useState(false);
    const [suggestionText, setsuggestionText] = useState('');

    const mailjet = require ('node-mailjet')
.connect('435af34a6c37852589c032923ecff7f5', 'b3599bbd28f8c70f0acc5b67484f0c33')
const request = mailjet
.post("send", {'version': 'v3.1'})
.request({
  "Messages":[
    {
      "From": {
        "Email": "gaurav.sharma4747@gmail.com",
        "Name": "gaurav"
      },
      "To": [
        {
          "Email": "gaurav.sharma4747@gmail.com",
          "Name": "gaurav"
        }
      ],
      "Subject": "Greetings from Mailjet.",
      "TextPart": "My first Mailjet email",
      "HTMLPart": "<h3>Dear passenger 1, welcome to <a href='https://www.mailjet.com/'>Mailjet</a>!</h3><br />May the delivery force be with you!",
      "CustomID": "AppGettingStartedTest"
    }
  ]
})
request
  .then((result) => {
    console.log(result.body)
  })
  .catch((err) => {
    console.log(err.statusCode)
  })

    return (
        openSuggestion ? <div className = 'suggestion-feedback suggestion-input-box'>
            <div className = 'form-area'>
                <label>Share your song suggestion</label>
            <textarea placeholder = 'share link from youtube/spotify/wynk...' rows="5" cols="40" value = {suggestionText} 
            onChange = {(e) => setsuggestionText(e.target.value)} />
            <div className = 'suggestion-action-btn'>
            <div className = 'submit-btn'>Submit</div>
            <div className = 'submit-btn suggestion-close-btn' onClick = {() => setopenSuggestion(false)}>Close</div>
            </div>
            </div>
        </div> : <div className = 'suggestion' onClick = {() => setopenSuggestion(true)}>Suggest a song?</div>
    )
}
