import {useEffect, useState, useRef} from 'react'
import axios from 'axios';
import YouTube from 'react-youtube';
import './App.css';

function App() {
  const [songs, setsongs] = useState([]);
  const [currentPlaying, setcurrentPlaying] = useState(null);
  const myRef = useRef(null);

  useEffect(() => {
     axios({
      url: 'https://www.googleapis.com/youtube/v3/playlistItems?part=snippet%2CcontentDetails&maxResults=9&playlistId=PLcjXjwgJpOzZoOYXz8y3I2PE_HspGkUry&key=AIzaSyA43Saqt5kUkQwm-BV_tWWwgA8HP5bwbXE',
      method: 'get'
    }).then(response => setsongs(response.data.items));
    //console.log(songs);
  }, []);
  const executeScroll = () => myRef.current.scrollIntoView();
  const opts = {
    height: '500',
    width: '100%',
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
    },
  };
  console.log(songs);
  return (
    <div className = {'app-wrapper'}><YouTube ref={myRef} videoId={currentPlaying.contentDetails.videoId} opts={opts} />
    <div className = {'list-container'}>
      {songs.map(item => <div key  = {item.id}>{
        <div className = {'List-Item'}>
          <img alt = 'video-thumbnail'  src = {item.snippet.thumbnails.default.url} />
          <div className = {'list-title'}>{item.snippet.title}</div>
          <div className = {'list-item-action-btn'}><button onClick = {()=>setcurrentPlaying(item)}>Play</button></div>
        </div>
        }</div>)}
    </div>
    </div>
  );
}

export default App;
