            onChange = {(e) => setsuggestionText(e.target.value)} />
        <div className = 'submit-btn'>Submit</div>
            </div>

        </div> 
        // <div className = 'suggestion' onclick = {() => setopenSuggestion(true)}>Suggest a song?</div>
