import React,{useState} from 'react'
import {AiFillCloseCircle} from 'react-icons/ai'

export default function Suggestion() {
    const [openSuggestion, setopenSuggestion] = useState(false);
    const [suggestionText, setsuggestionText] = useState('');
    return (
        openSuggestion ? <div className = 'suggestion-feedback suggestion-input-box'>
            <div className = 'suggestionClose'><AiFillCloseCircle /></div>
            <div className = 'form-area'>
                <label>Share your song suggestion</label>
            <textarea placeholder = 'share link from youtube/spotify/wynk...' rows="5" cols="40" value = {suggestionText} 
            onChange = {(e) => setsuggestionText(e.target.value)} />
            <div className = 'submit-btn'>Submit</div>
            </div>
        </div> : <div className = 'suggestion' onClick = {() => setopenSuggestion(true)}>Suggest a song?</div>
    )
}
